mapper.select:
SELECT s_no sNo, s_name sName, s_sdate sSdate, s_edate sEdate, s_wdate sWdate, s_rate sRate, s_cost sCost FROM schedule
WHERE
s_area like #{sarea}
and
to_char(s_sdate, 'MM') = #{month}

SELECT s_no sNo, s_name sName, s_sdate sSdate, s_edate sEdate, s_wdate sWdate, s_rate sRate, s_cost sCost FROM schedule
WHERE
s_area like #{sarea}
and
to_char(s_sdate, 'MM') = #{month}
ORDER BY s_wdate desc

SELECT s_no sNo, s_name sName, s_sdate sSdate, s_edate sEdate, s_wdate sWdate, s_rate sRate, s_cost sCost FROM schedule
WHERE
s_area like #{sarea}
and
to_char(s_sdate, 'MM') = #{month}
ORDER BY s_rate desc
